import React from 'react'


const TeamCard = (props) => {
    var backgColor="white"

    if(props.data.job === " "){
        props.data.job="not found"
        backgColor="red"
    }
    var bgColor="white"
    if(props.data.email === " "){
        props.data.email="not found"
        bgColor="red"
    }

    if(props.data.adress === " "){
      props.data.adress="not found"
      backgColor="red"
  }
  return (
    <div class="card bg-secondary mb-3" style={{width:"280px",marginTop:"50px",display:"inline-block",marginRight:"20px",marginLeft:"30px"}}>
       <div class='row' style={{textAlign:"center",width:"300px"}}>
        <img src={props.data.img} alt="photo" style={{height:"200px"}} />
        <h2>{props.data.name}</h2>
        <ul>
            <li class="card border-success mb-3" style={{backgroundColor: backgColor}} >{props.data.job}</li>
            <li class="card border-success mb-3"style={{backgroundColor: backgColor}} >{props.data.adress}</li>
            <li class="card border-success mb-3" style={{backgroundColor: backgColor}} >{props.data.email}</li>
        </ul>
        </div>  
    </div>
  )
}

export default TeamCard
