import React,{Component} from "react"

class Header extends Component{
  constructor(){
    super()
    this.state={
      count: 0
    }
    this.clickHandel=this.clickHandel.bind(this)
  }

  clickHandel(){
    this.setstate(
    function(){return {count: this.state.count + 1}}
    )
  }

  render(){
   return <>
   <p>{this.state.count}</p>
   <button onClick={this.clickHandel}>click me</button>
   </>
  }
}

export default Header
